select `un_ami_pour_la_vie`.`user`.`id` AS `id`,`un_ami_pour_la_vie`.`user`.`isActive` AS `isActive`,if((`un_ami_pour_la_vie`.`user`.`isActive` = TRUE),'Activé','Gelé') AS `status`,`un_ami_pour_la_vie`.`role`.`idROLE` AS `idROLE`,`un_ami_pour_la_vie`.`role`.`roleName` AS `roleName` from (`un_ami_pour_la_vie`.`user` left join `un_ami_pour_la_vie`.`role` on((`un_ami_pour_la_vie`.`user`.`role` = `un_ami_pour_la_vie`.`role`.`idROLE`)))